        "name": "<font color='#FFBB63'>강풍 베기</font>",
        "slotData": {
          "iconGrade": 10,
          "iconPath": "https://cdn-lostark.game.onstove.com/efui_iconatlas/tripod_tier/tripod_tier_3_39.png",
